import React,{ useState } from 'react';
import Card from '../UI/Card'
import classes from './AddUser.module.css';
import Button from '../UI/Button';


const formHandler = (props) => {
  const [userinput,setuserinput] = useState(userinput);
  const [ageinput,setageinput] = useState('');

  
  // const onuserinput () => {
  //   setuserinput (previnput => ...previnput +userinput);
  // }
  const onageinput = (userinput) => {
    setuserinput (input=> userinput +input);
  };

  const usernamehandler = (event) => {
       event.preventDefault();
       console.log(userinput,ageinput);
    }
    return (
      <Card className={`${classes.input} ${usernamehandler}`}>
        < form OnSubmit={usernamehandler}>
          <div>
        <label hmtlfor="username">USER NAME</label>
    <input type="text" id="1" onChange={onuserinput}></input>
  </div>
  <div>
    <label htmlfor="age">AGE</label>
    <input type="age" id="2" onChange={onageinput}></input>
  </div>
  <div>
    <Button type='submit'>ADD USER</Button>
  </div>
        </form>
      </Card>

    );
}
export default formHandler;
